'use strict';

module.exports = require('./lib/express');


var express = require('express');
var app = express();
app.use(express.static('/public/pictures'));
app.set('view engine', 'jade');
app.get('/',function(req,res){
	res.render('index.jade');
});

app.get('/cruz',function(req,res){
	res.sendfile('cruzMap.js');
	res.render('./rMap.jade');
});
app.get('/bernie',function(req,res){
	res.sendfile('bernieMap.js');
	res.render('./dMap.jade');
});
app.get('/hilary',function(req,res){
	res.sendfile('hilaryMap.js');
	res.render('./dMap.jade');
});
app.get('/trump',function(req,res){
	res.sendfile('trumpMap.js');
	res.render('./rMap.jade');
});
app.get('/rubio',function(req,res){
	res.sendfile('rubioMap.js');
	res.render('./rMap.jade');
});
app.get('/map',function(req,res){
	res.render('map.jade');
});

app.listen(3000, function () {
  console.log('Example app listening on port 3000!');
});
